import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { FindUserDTO } from 'src/app/Services/user.service';
let ContactListComponent = class ContactListComponent {
    constructor(db, DataService, userService) {
        this.contactList = [];
        // Search variables
        this.searchUserLogin = new FindUserDTO();
        this.db = db;
        this.DataService = DataService;
        this.userService = userService;
    }
    async ngOnInit() {
        await this.fillContactList();
    }
    async fillContactList() {
        this.contactList = await this.db.Contacts.toArray();
    }
    ChangeDisplayedMessages(newDesplayedUserLogin) {
        this.DataService.currentDisplayedLogin.next(newDesplayedUserLogin);
    }
    async searchUsers() {
        if (this.searchUserLogin.login == "") {
            return;
        }
        let findedUsers = await this.userService.FindUsers(this.searchUserLogin); // TODO take preloader
        let newDispayedUsers = [];
        findedUsers.forEach(element => {
            var newContact = { Login: element };
            newDispayedUsers.push(newContact);
        }); //TODO On click enter to input => create subscribe to search start
        $(".users").hide(500);
        setTimeout(() => {
            this.contactList = newDispayedUsers;
            $(".searchUsersDisplay").show(500);
        }, 500);
    }
    async backToContactListButton() {
        $(".searchUsersDisplay").hide(500);
        await this.fillContactList();
    }
};
ContactListComponent = __decorate([
    Component({
        selector: 'contact-list-component',
        templateUrl: './contact.list.component.html',
        styleUrls: ['./contact.list.component.css']
    })
], ContactListComponent);
export { ContactListComponent };
