import { __decorate, __param } from "tslib";
import { Component, Inject } from '@angular/core';
import { db } from 'src/db';
let RegisterUserComponent = class RegisterUserComponent {
    constructor(http, baseUrl, rsaService) {
        this.http = http;
        this.currentUser = { Login: "" };
        this.baseUrl = baseUrl;
        this.rsaService = rsaService;
    }
    async ngOnInit() {
        var user = await db.User.toArray();
        if (user.length != 0) {
            this.currentUser = user[0];
            $("#inputLogin").prop("disabled", true);
            $("#sendButton").hide();
        }
    }
    async RegisterUser() {
        if (this.currentUser.Login == "") {
            // TODO add notify
            return;
        }
        await this.http.post(this.baseUrl + "user", this.currentUser).subscribe(result => this.DisplayNotify(result));
        return;
    }
    async DisplayNotify(resultRequest) {
        if (resultRequest == "true") { // TODO добавить сразу в localDB
            await db.User.add(await this.currentUser, 0);
            console.log(resultRequest + "true");
            $("#inputLogin").prop("disabled", true);
            $("#sendButton").hide();
            $("#succesNotify").show(500);
            setTimeout(() => {
                $("#succesNotify").hide(500);
            }, 2500);
        }
        else {
            console.log(resultRequest + "false");
            $("#dangerNotify").show(500);
            setTimeout(() => {
                $("#dangerNotify").hide(500);
            }, 2500);
        }
    }
};
RegisterUserComponent = __decorate([
    Component({
        selector: 'register-user-component',
        templateUrl: 'registerUserComponent.html',
        providers: []
    }),
    __param(1, Inject('BASE_URL'))
], RegisterUserComponent);
export { RegisterUserComponent };
