import Dexie from 'dexie';
var privateKey;
var publicKey;
export class AppDB extends Dexie {
    constructor() {
        super('criptedOnlineChatDB');
        this.version(3).stores({
            User: '++id, login',
            Messages: '++id, data, dateTime, recipientLogin',
            Contacts: '++id, login, privateKey, publicKey'
        });
        this.on('populate', () => this.populate());
    }
    async populate() {
        await db.Contacts.bulkAdd([
            {
                Login: "Bagike",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "North",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "West",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "LosAngeles",
                PrivateKey: privateKey,
                PublicKey: publicKey
            }
        ]);
        await db.Messages.bulkAdd([
            {
                Data: "",
                DateTime: "",
                RecipientLogin: ""
            },
            {
                Login: "North",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "West",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "LosAngeles",
                PrivateKey: privateKey,
                PublicKey: publicKey
            }
        ]);
    }
}
export const db = new AppDB();
