import { __decorate } from "tslib";
import { Component, Input } from '@angular/core';
let MessageListComponent = class MessageListComponent {
    constructor(db, dataService, webSocketService) {
        this.messageList = [];
        this.user = { Login: "" };
        this.sendedMessage = "";
        this.DataService = dataService;
        this.db = db;
        this.WebSocketService = webSocketService;
    }
    ngDoCheck() {
        if (this.messageList.length == 0) {
            $(".messageClass").hide();
        }
        else {
            $(".messageClass").show();
        }
    }
    async ngOnInit() {
        await this.DataService.currentDisplayedLogin.subscribe((currentDisplayedLogin) => this.DisplayMissages(currentDisplayedLogin));
        await this.db.User.toArray().then(x => this.user = x[0]);
    }
    async DisplayMissages(dispayedMessagesLogin) {
        await this.db.Messages.where("RecipientLogin").equalsIgnoreCase(dispayedMessagesLogin).toArray().then(x => this.messageList = x);
    }
    async SendMessage(recipientLogin) {
        if (this.sendedMessage == "") {
            return;
        }
        this.WebSocketService.sendMessage(this.sendedMessage, recipientLogin);
    }
};
