import * as i0 from "@angular/core";
import * as i1 from "src/db";
export class MessageService {
    constructor(AppDB) {
        this.AppDB = AppDB;
    }
    async SendMessage(rsaService) {
        this.rsaService = rsaService;
    }
    async AddNewMessages(addedmessages) {
        addedmessages.forEach(async (element) => {
            let recipientLogin = (await this.AppDB.Contacts.filter(x => x.ContactId == element.senderId).first()).Login;
            let recipientPrivateKeyId = (await this.AppDB.Contacts.filter(x => x.ContactId == element.senderId).first()).PrivateKeyId;
            let privateKeyForDecript = await this.AppDB.PrivateKey.filter(x => x.id == recipientPrivateKeyId).first();
            let decriptedMessage = await this.rsaService.DecriptMessage(element.data, privateKeyForDecript);
            let newMessage = { Data: decriptedMessage, RecipientLogin: recipientLogin, IsSender: false };
            await this.AppDB.Messages.add(newMessage);
        });
    }
}
