import Dexie from 'dexie';
import * as i0 from "@angular/core";
var privateKey;
var publicKey;
export class AppDB extends Dexie {
    constructor() {
        super('criptedOnlineChatDB');
        this.version(3).stores({
            User: '++id, Login',
            Messages: '++id, Data, DateTime, RecipientLogin',
            Contacts: '++id, Login, PrivateKey, PublicKey'
        });
        this.on('populate', () => this.populate());
    }
    async populate() {
        await db.Contacts.bulkAdd([
            {
                Login: "Bagike",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "North",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "West",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "LosAngeles",
                PrivateKey: privateKey,
                PublicKey: publicKey
            }
        ]);
        await db.Messages.bulkAdd([
            {
                Data: "Привет, это бажик",
                DateTime: "18:11",
                RecipientLogin: "Bagike",
                Sender: true
            },
            {
                Data: "Отправляю тебе тестовое сообщение",
                DateTime: "18:12",
                RecipientLogin: "Bagike",
                Sender: true
            },
            {
                Data: "",
                DateTime: "",
                RecipientLogin: "North",
                Sender: true
            },
            {
                Data: "",
                DateTime: "",
                RecipientLogin: "North",
                Sender: true
            }
        ]);
    }
}
