import * as forge from 'node-forge';
import * as i0 from "@angular/core";
import * as i1 from "src/db";
import * as i2 from "../../Services/data.service";
import * as i3 from "../../Services/websocket.service";
import * as i4 from "src/app/Services/rsa.service";
export class MessageListComponent {
    constructor(db, dataService, webSocketService, rsaService) {
        // variables to display
        this.messageList = [];
        this.user = { Login: "" };
        this.isKeyExchangeCompleted = false;
        // variables to html
        this.sendedMessage = "";
        this.dispayedMessagesLogin = "";
        this.DataService = dataService;
        this.db = db;
        this.WebSocketService = webSocketService;
        this.rsaService = rsaService;
    }
    ngDoCheck() {
        if (this.isKeyExchangeCompleted) {
            $(".messageClass").hide();
        }
        else {
            $(".messageClass").show();
        }
    }
    async ngOnInit() {
        await this.DataService.currentDisplayedLogin.subscribe((currentDisplayedLogin) => {
            this.DisplayMissages(currentDisplayedLogin);
            this.dispayedMessagesLogin = currentDisplayedLogin;
            $('.recipientName').text(currentDisplayedLogin);
        });
        await this.db.User.toArray().then(x => this.user = x[0]);
        $(".NoKeyMessage").hide();
        $(".MessageClass").hide();
    }
    async DisplayMissages(dispayedMessagesLogin) {
        // Проверяем, есть ли у отображаемого контакта ключ для шифровки сообщения, если нет, то скрываем компонент отправки сообщения, и отображаем контейнер информирования
        var displayedContact = await this.db.Contacts.filter(x => x.Login == dispayedMessagesLogin).first();
        if (displayedContact.PublicKeyForEncriptId == undefined) {
            $(".NoKeyMessage").show();
            $(".MessageClass").hide();
        }
        else {
            $(".NoKeyMessage").hide();
            $(".MessageClass").show();
        }
        // выгрузка сообщений из localdb и отображение их
        this.messageList = [];
        var messagesToDisplay = await this.db.Messages.filter(x => x.RecipientLogin == dispayedMessagesLogin).toArray();
        messagesToDisplay.forEach(async (element) => {
            let decriptedNewMessage = { Data: element.Data, IsSender: element.IsSender, RecipientLogin: element.RecipientLogin };
            this.messageList.push(decriptedNewMessage);
        });
    }
    async SendMessage() {
        if (this.sendedMessage == "") {
            return;
        }
        let contactForSend = await this.db.Contacts.filter(x => x.Login == this.dispayedMessagesLogin).first();
        let key = await this.db.PublicKeyForEncript.filter(x => x.id == contactForSend.PublicKeyForEncriptId).first();
        var keyForEncript = forge.pki.rsa.setPublicKey(key.n, key.e);
        let encriptedMessage = await this.rsaService.EncriptMessage(this.sendedMessage, keyForEncript);
        let message = {
            data: encriptedMessage, senderId: this.user.id,
            recipientId: contactForSend.ContactId
        };
        await this.WebSocketService.sendMessage(message);
        let addedMessage = { Data: this.sendedMessage, RecipientLogin: contactForSend.Login, IsSender: true };
        await this.db.Messages.add(addedMessage);
        this.messageList = [];
        this.sendedMessage = "";
        await this.DisplayMissages(contactForSend.Login);
    }
