import * as forge from 'node-forge';
import * as i0 from "@angular/core";
export class RSAService {
    constructor() {
        this.privateKey = new forge.pki.rsa.PrivateKey;
    }
    async InitService() {
        let publicKey;
        let privateKey;
        this.GenerateKeyPair().then(x => {
            privateKey = x.privateKey;
            publicKey = x.publicKey;
        });
        forge.pki.rsa.setPrivateKey(privateKey.n, privateKey.e, privateKey.d, privateKey.p, privateKey.q, privateKey.dP, privateKey.dQ, privateKey.qInv);
        forge.pki.rsa.setPublicKey(publicKey.n, publicKey.e);
    }
    async GenerateKeyPair() {
        let keyPair = await forge.pki.rsa.generateKeyPair({ bits: 2048, e: 0x10001 });
        return keyPair;
    }
    async EncriptMessage(message) {
        if (this.publicKey != undefined) {
            let encriptedMessage = await this.publicKey.encrypt(message);
            return encriptedMessage;
        }
        else {
            return "undefined"; // убрать костыль
        }
    }
    async DecriptMessage(message) {
        if (this.privateKey != undefined) {
            let decriptedMessage = await this.privateKey.decrypt(message);
            return decriptedMessage;
        }
        else {
            return "undefined"; // тут то же убрать
        }
    }
}
