import * as signalR from '@microsoft/signalr';
import { HubConnectionBuilder } from '@microsoft/signalr';
import * as i0 from "@angular/core";
import * as i1 from "src/db";
import * as i2 from "./message.service";
import * as i3 from "./rsa.service";
import * as i4 from "./rsa.keys.service";
export class WebSocketService {
    constructor(baseUrl, appDB, messageService, RSAService, RSAKeysService) {
        this.baseUrl = baseUrl + "schatHub";
        this.messageService = messageService;
        this.appDB = appDB;
        this.RSAService = RSAService;
        this.HubConnection = new HubConnectionBuilder()
            .configureLogging(signalR.LogLevel.Debug)
            .withUrl("http://localhost:5172/schatHub")
            .build();
        this.HubConnection.start();
        this.initWebSocket();
    }
    async initWebSocket() {
        await this.HubConnection.on("Test", message => { console.log(message); });
        await this.HubConnection.on("AddNewRSAKeys", rsaKeys => { this.RSAKeysService.AddNewRSAKeysServer(rsaKeys); });
        await this.HubConnection.on("AddNewMessages", messages => { this.messageService.AddNewMessages(messages); });
    }
    async sendMessage(message) {
        await this.HubConnection.send("SendMessage", message);
    }
    async SendPublicRSAKey(rsaKey) {
        await this.HubConnection.send("SendRSAKeys", rsaKey);
    }
}
