import * as signalR from '@microsoft/signalr';
import { HubConnectionBuilder } from '@microsoft/signalr';
import * as forge from 'node-forge';
import { v4 as uuidv4 } from 'uuid';
import * as i0 from "@angular/core";
import * as i1 from "src/db";
export class WebSocketService {
    constructor(baseUrl, appDB) {
        this.baseUrl = baseUrl + "schatHub";
        this.appDB = appDB;
        this.HubConnection = new HubConnectionBuilder()
            .configureLogging(signalR.LogLevel.Debug)
            .withUrl("http://localhost:5172/schatHub")
            .build();
        this.HubConnection.start();
        this.initWebSocket();
    }
    async initWebSocket() {
        await this.HubConnection.on("Test", message => { console.log(message); });
        await this.HubConnection.on("AddNewRSAKeys", rsaKeys => { this.AddNewRSAKeysServer(rsaKeys); });
    }
    async sendMessage(message, recipientLogin) {
        await this.HubConnection.send("SendMessage", message, recipientLogin);
    }
    async SendPublicRSAKey(rsaKey) {
        await this.HubConnection.send("SendRSAKeys", rsaKey);
    }
    async AddNewRSAKeysServer(keys) {
        keys.forEach(async (element) => {
            let insertedPubKey = { id: uuidv4() };
            let n = new forge.jsbn.BigInteger(null);
            n.s = element.ns;
            n.t = element.nt;
            n.data = JSON.parse(element.nDataJson);
            let e = new forge.jsbn.BigInteger(null);
            e.s = element.es;
            e.t = element.et;
            e.data = JSON.parse(element.eDataJson);
            insertedPubKey.e = e;
            insertedPubKey.n = n;
            this.appDB.PublicKeyForEncript.add(insertedPubKey);
            let isContaints = await this.appDB.Contacts.filter(x => x.ContactId == element.senderUserId).count();
            if (isContaints == 0) {
                let newContact = { Login: element.senderLogin };
                newContact.ContactId = element.senderUserId;
                newContact.Login = element.senderLogin;
                this.appDB.Contacts.add(newContact);
            }
        });
    }
}
