            $("#sendButton").hide();
            $("#succesNotify").show(500);
            setTimeout(() => {
                $("#succesNotify").hide(500);
            }, 2500);
        }
        else {
            console.log(resultRequest + "false");
            $("#dangerNotify").show(500);
            setTimeout(() => {
                $("#dangerNotify").hide(500);
            }, 2500);
        }
    }
}
