import { __decorate } from "tslib";
import Dexie from 'dexie';
var privateKey;
var publicKey;
let AppDB = class AppDB extends Dexie {
    constructor() {
        super('criptedOnlineChatDB');
        this.version(3).stores({
            User: '++id, login',
            Messages: '++id, data, dateTime, recipientLogin',
            Contacts: '++id, login, privateKey, publicKey'
        });
        this.on('populate', () => this.populate());
    }
    async populate() {
        await db.Contacts.bulkAdd([
            {
                Login: "Bagike",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "North",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "West",
                PrivateKey: privateKey,
                PublicKey: publicKey
            },
            {
                Login: "LosAngeles",
                PrivateKey: privateKey,
                PublicKey: publicKey
            }
        ]);
        await db.Messages.bulkAdd([
            {
                Data: "",
                DateTime: "",
                RecipientLogin: "Bagike"
            },
            {
                Data: "",
                DateTime: "",
                RecipientLogin: "Bagike"
            },
            {
                Data: "",
                DateTime: "",
                RecipientLogin: "North"
            },
            {
                Data: "",
                DateTime: "",
                RecipientLogin: "North"
            }
        ]);
    }
};
AppDB = __decorate([
    Injectable({ providedIn: 'root' })
], AppDB);
export { AppDB };
export const db = new AppDB();
