export class MessageListComponent {
    constructor(db, dataService, webSocketService, rsaService) {
        // variables to display
        this.messageList = [];
        this.user = { Login: "" };
        this.isKeyExchangeCompleted = false;
        // variables to html
        this.sendedMessage = "";
        this.dispayedMessagesLogin = "";
        this.DataService = dataService;
        this.db = db;
        this.WebSocketService = webSocketService;
        this.rsaService = rsaService;
    }
    ngDoCheck() {
        if (this.isKeyExchangeCompleted) {
            $(".messageClass").hide();
        }
        else {
            $(".messageClass").show();
        }
    }
    async ngOnInit() {
        await this.DataService.currentDisplayedLogin.subscribe((currentDisplayedLogin) => {
            this.DisplayMissages(currentDisplayedLogin);
            this.dispayedMessagesLogin = currentDisplayedLogin;
            $('.recipientName').text(currentDisplayedLogin);
        });
        await this.db.User.toArray().then(x => this.user = x[0]);
    }
    async DisplayMissages(dispayedMessagesLogin) {
        let privateKeyId = (await this.db.Contacts.filter(x => x.Login == dispayedMessagesLogin).first()).PrivateKeyId;
        let prKey = await this.db.PrivateKey.filter(x => x.id == privateKeyId).first();
        let keyForDecript = forge.pk;
        var messagesFromDecript;
        await this.db.Messages.where("RecipientLogin").equalsIgnoreCase(dispayedMessagesLogin).toArray().then(x => messagesFromDecript = x);
        messagesFromDecript.forEach(async (element) => {
            let decriptedMessages = await this.rsaService.DecriptMessage(element.Data);
        });
    }
    async SendMessage() {
        if (this.sendedMessage == "") {
            return;
        }
        let contactForSend = await this.db.Contacts.filter(x => x.Login == this.dispayedMessagesLogin).first();
        let key = await this.db.PublicKeyForEncript.filter(x => x.id == contactForSend.PublicKeyForEncriptId).first();
        var keyForEncript = forge.pki.rsa.setPublicKey(key.n, key.e);
        let encriptedMessage = await this.rsaService.EncriptMessage(this.sendedMessage, keyForEncript);
        let message = {
            data: encriptedMessage, senderId: this.user.id,
            recipientId: contactForSend.ContactId
        };
        this.WebSocketService.sendMessage(message); // TODO добавить отображение сообщения
    }
